    return <h1> profile </h1>
}
Task.getInitialProps=async(ctx)=>{
   const res =await fetch ('https://jsonplaceholder.typicode.com/posts/'+ctx.query.id)
   const resJSON =await res.json();
    return {user:resJSON.data}
}
